mapper.update:
update peer_evaluation
set grade=#{grade},comments=#{comments}
where eva_sid=#{evaSid}
and sid=#{sid}
and wid=#{wid}
and cid=#{cid}

update stu_homework
set teacherGrade=#{teacherGrade},teacherComments=#{teacherComments}
where sid=#{sid}
and wid=#{wid}
and cid=#{cid}

mapper.select:
select * from peer_evaluation
where evaSid=#{evaSid}
and wid=#{wid}

select * from peer_evaluation
where sid=#{sid}
and cid=#{cid}
and wid=#{wid}

select * from stu_homework
where sid=#{sid}
and cid=#{cid}
and wid=#{wid}

select * from stu_homework
where sid=#{sid}
and cid=#{cid}
and wid=#{wid}
