mapper.select:
select id,name,details,url,submit_time,is_late,teacher_grade,total_grade
from stu_homework,student
where stu_homework.sid=student.id
and wid=#{wid}
and cid=#{cid}

mapper.update:
update stu_homework
set teacher_grade=#{teacherGrade},teacher_comments=#{teacherComments},total_grade=#{tempGrade}
where sid=#{sid}
and wid=#{wid}
and cid=#{cid}
